// Extract the 'reply' field from the input JSON
let replyText = $json.reply;

// Ensure that we only return the reply text
return [
  {
    "text": replyText
  }
];

let output;

try {
    // Try to parse the JSON from the Manager response
    output = JSON.parse($json.text);
} catch (error) {
    // If parsing fails, handle it as a regular text message
    return {
        route: "manager",  // Default route for handling non-JSON inputs
        reply: $json.text  // Send the raw text as the reply
    };
}

// Return both the route and the reply for further processing if JSON parsing succeeds
return {
    route: output.route,   // Keep route for the Switch node
    reply: output.reply    // Send reply to the Respond webhook
};

let output;

// Extract the message from the Chat_Visio input and rename it as chatInput
if ($json.body && $json.body.message) {
  output = {
    chatInput: $json.body.message,  // Rename message to chatInput
  };
} else {
  output = {
    chatInput: "No chatInput found",  // Handle case where message is missing
  };
}

return output;

const commandSchema = {
  "type": "object",
  "properties": {
    "command": {
      "type": "string",
      "description": "The command to be executed in Visio. Currently, only 'CreateShape' is supported."
    },
    "parameters": {
      "type": "object",
      "properties": {
        "shapes": {
          "type": "array",
          "description": "An array of shapes to be created. Each object in the array represents a single shape.",
          "items": {
            "type": "object",
            "properties": {
              "shapeType": {
                "type": "string",
                "description": "The type of shape to create (e.g., 'rectangle', 'circle', 'triangle')."
              },
              "position": {
                "type": "object",
                "properties": {
                  "x": {
                    "type": "number",
                    "description": "The x-coordinate of the shape's position on the canvas (0-100)."
                  },
                  "y": {
                    "type": "number",
                    "description": "The y-coordinate of the shape's position on the canvas (0-100)."
                  }
                },
                "required": ["x", "y"],
                "additionalProperties": false
              },
              "size": {
                "type": "object",
                "properties": {
                  "width": {
                    "type": "number",
                    "description": "The width of the shape."
                  },
                  "height": {
                    "type": "number",
                    "description": "The height of the shape."
                  }
                },
                "required": ["width", "height"],
                "additionalProperties": false
              },
              "color": {
                "type": "string",
                "description": "The color of the shape (e.g., 'red', 'blue', 'green')."
              }
            },
            "required": ["shapeType", "position", "size"],
            "additionalProperties": false
          }
        }
      },
      "required": ["shapes"],
      "additionalProperties": false
    }
  },
  "required": ["command", "parameters"],
  "additionalProperties": false
};

return {
  schema: JSON.stringify(commandSchema)
};
